        printf(&quot;1. Aller à la banque\n&quot;);
        printf(&quot;2. Aller au magasin\n&quot;);
        printf(&quot;3. Quitter\n&quot;);
        printf(&quot;Votre choix : &quot;);
        scanf(&quot;%d&quot;, &amp;choix);

        if (choix == 1) {
            banque();
        } else if (choix == 2) {
            magasin();
        } else if (choix == 3) {
            printf(&quot;Au revoir !\n&quot;);
            exit(0);
