def func(p):
    # Simulating the func() behavior
    v1 = p + 2
    v2 = 2398 * p + 2400

    # Final calculation according to the original C logic
    v3 = 3 * (v1 &amp; ~v2) + 2 * p * ~(v2 &amp; v1)
