// ----- helpers -----
static inline uint64_t rotl64(uint64_t x, unsigned r) {
  return (x &lt;&lt; (r &amp; 63)) | (x &gt;&gt; ((64 - r) &amp; 63));
}

// ----- shadow protocol core -----
static uint64_t shadow_protocol(uint64_t in) {
  uint32_t hi = (uint32_t)(in &gt;&gt; 32);
  uint32_t lo = (uint32_t)in;

  uint32_t round_keys[4] = {0xa5a5c3c3U, 0x5a5a9696U, 0x3c3ca5a5U, 0xc3c35a5aU};

  for (int r = 0; r &lt; 8; ++r) {
    uint32_t t =
      ((uint32_t)sbox[(lo      ) &amp; 0xFF]      ) |
      ((uint32_t)sbox[(lo &gt;&gt; 24) &amp; 0xFF] &lt;&lt; 24) |
      ((uint32_t)sbox[(lo &gt;&gt; 16) &amp; 0xFF] &lt;&lt; 16) |
      ((uint32_t)sbox[(lo &gt;&gt;  8) &amp; 0xFF] &lt;&lt;  8);

    t ^= round_keys[r % 4];

    uint32_t mix = (uint32_t)((uint64_t)(r + 1) * 0x1337BEEFULL) ^ hi;

    uint32_t t_rot = (t &gt;&gt; ((29 - r) &amp; 31)) | (t &lt;&lt; ((r + 3) &amp; 31));
    uint32_t new_lo = t_rot + mix ^ hi;

    hi = lo;
    lo = new_lo;
  }

  uint64_t merged = (((uint64_t)hi) &lt;&lt; 32) | lo;
  uint64_t x = merged ^ 0xDEADBEEFCAFEBABEULL;
  return rotl64(x, 17) + 0x1234567890ABCDEFull;
}

// ----- bit tree -----
typedef struct Node {
  uint8_t value;   // leaf&#039;s 3-bit value in lower bits; 0 for internal
  uint8_t _pad[7];
  struct Node* left;
  struct Node* right;
} Node;

static Node* build_bittree(uint64_t src, int lo, int hi) {
  if (hi &lt; lo) return NULL;

  Node* n = (Node*)malloc(sizeof(Node));
  if (!n) return NULL;
  memset(n, 0, sizeof(*n));

  if (lo == hi) {
    int idx = hi * -3 + 0x3F; // copied logic
    uint8_t v = 7;
    if (idx &gt;= 0) {
      if (idx &lt; 2) {
        if (idx == 1) v = (uint8_t)(((src &amp; 0xFF) * 2) &amp; 6U) | 1U;
        else /* idx==0 */ v = (uint8_t)(((uint32_t)src &amp; 1U) &lt;&lt; 2) | 3U;
      } else {
        v = (uint8_t)((src &gt;&gt; ((idx - 2) &amp; 63)) &amp; 7ULL);
      }
    }
    n-&gt;value = v;
    n-&gt;left = n-&gt;right = NULL;
  } else {
    int mid = (lo + hi) / 2;
    n-&gt;value = 0;
    n-&gt;left  = build_bittree(src, lo,  mid);
    n-&gt;right = build_bittree(src, mid + 1, hi);
  }
  return n;
}

static void shadow_tree_mix(const Node* n, uint64_t* acc, int* leaves) {
  if (!n) return;
  shadow_tree_mix(n-&gt;left,  acc, leaves);
  shadow_tree_mix(n-&gt;right, acc, leaves);
  if (!n-&gt;left &amp;&amp; !n-&gt;right) {
    *acc = (*acc * 8ULL) | (uint64_t)(n-&gt;value &amp; 7U);
    (*leaves)++;
  }
}

static void free_bittree(Node* n) {
  if (!n) return;
  free_bittree(n-&gt;left);
  free_bittree(n-&gt;right);
  free(n);
}

// ----- debug: send value to 127.0.0.1:0xBAD7 -----
static void debug_send(uint64_t val) {
  int fd = socket(AF_INET, SOCK_STREAM, 0);
  if (fd &lt; 0) return;

  struct sockaddr_in sin;
  memset(&amp;sin, 0, sizeof(sin));
  sin.sin_family = AF_INET;
  sin.sin_port = htons(0xBAD7);
  sin.sin_addr.s_addr = htonl(0x7F000001UL); // 127.0.0.1

