from web3 import Web3

# Configuration parameters
RPC_URL = &quot;chall2.midnightflag.fr:11269/rpc&quot;
CHAIN_ID = 1337
private_key = &quot;0xca11ab1ec0ffee000002a575fa5f74540719ba065a610cba6497cdbf22cd5cdb&quot;
account_address = &quot;0x277506E301F0907b9bB7B954eB5B87aad9DABe92&quot;
# Ensure no trailing spaces
contract_address = &quot;0x0e759A2168d397bAcdAb026c15BfA219d325A6a3&quot;

# Connect to the RPC endpoint
w3 = Web3(Web3.HTTPProvider(RPC_URL))
if w3.is_connected():
    print(&quot;Connected to the RPC endpoint&quot;)
else:
    raise Exception(&quot;Unable to connect to the RPC endpoint&quot;)

# Minimal ABI for the DestroyAlderaan function
abi = [
    {
        &quot;inputs&quot;: [{&quot;internalType&quot;: &quot;string&quot;, &quot;name&quot;: &quot;_key&quot;, &quot;type&quot;: &quot;string&quot;}],
        &quot;name&quot;: &quot;DestroyAlderaan&quot;,
        &quot;outputs&quot;: [],
        &quot;stateMutability&quot;: &quot;payable&quot;,
        &quot;type&quot;: &quot;function&quot;
    }
]

# Instantiate the contract
contract = w3.eth.contract(address=contract_address, abi=abi)

# Define the correct key and the ETH amount to send
correct_key = &quot;ObiWanCantSaveAlderaan&quot;
# Sending a small nonzero value (adjust if needed)
value_to_send = w3.to_wei(0.0001, &#039;ether&#039;)

# Get the current nonce for the account
nonce = w3.eth.get_transaction_count(account_address)

# Build the transaction for calling DestroyAlderaan
tx = contract.functions.DestroyAlderaan(correct_key).build_transaction({
    &#039;chainId&#039;: CHAIN_ID,
    &#039;gas&#039;: 200000,  # Adjust the gas limit if necessary
    &#039;gasPrice&#039;: w3.eth.gas_price,
    &#039;nonce&#039;: nonce,
    &#039;value&#039;: value_to_send,
    &#039;from&#039;: account_address
})

# Sign the transaction using your private key
